{{#let this.features as |f|}}
  <HeadLayout />
  <div class="feature-wrapper {{if f.dashboard 'dashboard'}} {{if f.pride 'travis-pride'}} {{if f.comicSans 'travis-comicsans'}}">
    {{outlet}}
    <PageFooter />
  </div>
  {{#if f.tracer}}
    <XTracer />
  {{/if}}
  <IntercomIo />
{{/let}}

{{#if this.branchesExist}}
  <div class="branches">
    {{#if this.defaultBranch}}
      <section class="default-branch">
        <h2 class="small-title">
          Default Branch
        </h2>
        <ul class="blank-list">
          <BranchRow @branch={{this.defaultBranch}} />
        </ul>
      </section>
    {{/if}}
    {{#if this.activeBranches.length}}
      <section class="active-branches">
        <h2 class="small-title">
          Active Branches
        </h2>
        <ul class="blank-list">
          {{#each this.activeBranches as |branch|}}
            <BranchRow @branch={{branch}} />
          {{/each}}
        </ul>
      </section>
    {{/if}}
    {{#if this.inactiveBranches.length}}
      <section class="inactive-branches">
        <h2 class="small-title">
          Inactive Branches
        </h2>
        <ul class="blank-list">
          {{#each this.inactiveBranches as |branch|}}
            <BranchRow @branch={{branch}} @inactive={{true}} />
          {{/each}}
        </ul>
      </section>
    {{/if}}
  </div>
{{else}}
  <MissingNotice @title="No other branches for this repository" />
{{/if}}

{{#let this.build as |build|}}
  {{#if build.isMatrix}}
    {{#if build.stages}}
      {{#each build.sortedStages as |stage|}}
        <JobsList
          @stage={{stage}}
          @build={{build}}
          @repo={{build.repo}}
          @stages={{this.sortedBuildStages}}
        />
      {{/each}}
    {{else}}
      <JobsList
        @jobs={{build.requiredJobs}}
        @repo={{build.repo}}
        @required={{true}}
      />
      <JobsList @jobs={{build.allowedFailureJobs}} @repo={{build.repo}} />
    {{/if}}
  {{/if}}
{{/let}}

{{#if this.model.isLoaded}}
  <ul class="build-list">
    {{#each this.builds as |build|}}
      <BuildsItem @build={{build}} />
    {{else}}
      <NoBuilds />
    {{/each}}
  </ul>
  <div class="flex flex--center flex--space-between flex--v-center">
    {{#if this.showBuildHistoryLink}}
      <div>
        Previous build runs are available at
        <ExternalLinkTo
          @href={{this.buildHistoryLink}}
          class="link link--underline link--teal"
        >
          travis-ci.org
        </ExternalLinkTo>
      </div>
    {{/if}}
    {{#if this.displayShowMoreButton}}
      <p>
        {{#if this.loadMoreBuilds.isRunning}}
          <LoadingIndicator />
        {{else}}
          <ShowMoreButton @click={{perform this.loadMoreBuilds}} />
        {{/if}}
      </p>
    {{/if}}
  </div>
{{else}}
  <LoadingIndicator />
{{/if}}

{{#if this.cachesExist}}
  <div class="caches-header">
    <h1 class="small-title">
      All caches
      <small>
        (<ExternalLinkTo @href="http://docs.travis-ci.com/user/caching/" @title="Read about caching">Read the docs</ExternalLinkTo>)
      </small>
    </h1>
    <button
      class="{{if this.deleteRepoCache.isRunning 'deleting'}} delete-cache-button no-button"
      title="Delete all repository caches"
      onclick={{action (perform this.deleteRepoCache)}}
    >
      Delete all repository caches
      <SvgImage @name="icon-trash" @class="icon" />
    </button>
  </div>
  {{#if this.model.pushes.length}}
    <h2 class="small-title">
      Pushes
    </h2>
    <ul class="caches-list push-caches">
      {{#each this.model.pushes as |cache|}}
        <CachesItem
          @cache={{cache}}
          @repo={{this.repo}}
          @caches={{this.model.pushes}}
        />
      {{/each}}
    </ul>
  {{/if}}
  {{#if this.model.pullRequests.length}}
    <h2 class="small-title">
      {{vcs-vocab this.repo.vcsType 'pullRequest' plural=true}}
    </h2>
    <ul class="caches-list pull-request-caches">
      {{#each this.model.pullRequests as |cache|}}
        <CachesItem
          @cache={{cache}}
          @repo={{this.repo}}
          @caches={{this.model.pullRequests}}
        />
      {{/each}}
    </ul>
  {{/if}}
{{else}}
  <p class="helptext no-caches">
    No caches have been created yet,
    <br />
    read more on
    <ExternalLinkTo
      @href="http://docs.travis-ci.com/user/caching/"
      @title="Documentation on caching"
    >
      how to setup caching with your build
    </ExternalLinkTo>
    .
  </p>
{{/if}}

{{#if this.account.hasSubscriptionPermissions}}
  {{#if this.subscription.isResubscribable}}
    <button {{on 'click' (action (perform this.resubscribe))}} class='button--green mr-1' data-test-resubscribe-subscription='true'>
      Resubscribe to plan
    </button>
    <button {{on 'click' this.openPlansSelector}} class='button--white button--hover' data-test-resubscribe-change-plan='true'>
      Subscribe to different plan
    </button>
  {{else}}
    <button {{on 'click' this.openPlansSelector}} class='button--blue' data-test-change-subscription='true'>
      Change plan
    </button>
  {{/if}}
{{else}}
  {{#if this.subscription.isResubscribable}}
    <div class='button disabled' data-test-resubscribe-subscription-disabled='true'>
      Resubscribe to plan
    </div>
    <div class='button disabled' data-test-resubscribe-subscription-disabled='true'>
      Subscribe to different plan
    </div>
  {{else}}
    <div class='button disabled' data-test-change-plan-disabled='true'>
      Change plan
    </div>
  {{/if}}
{{/if}}

{{#if accounts.fetchSubscriptions.isRunning}}
  <LoadingIndicator/>
{{else}}
  <section class="billing">
    {{#if this.isEducation}}
      <Billing::Education 
        @newSubscription={{this.newSubscription}}
        @account={{this.account}}
      />
    {{/if}}
    {{#if this.isManual}}
      <BillingManual 
        @subscription={{this.subscription}} 
        @account={{this.account}} 
        @newSubscription={{this.newSubscription}}
      />
    {{/if}}
    {{#if this.isManaged}}
      <Billing::Subscription
        @newSubscription={{this.newSubscription}}
        @account={{this.account}}
      />
    {{/if}}
    {{#if this.isTrial}}
      <Billing::Trial 
        @account={{this.account}} 
        @newSubscription={{this.newSubscription}}
      />
    {{/if}}
  </section>
  {{#if this.invoices}}
    <Billing::Invoices 
      @invoices={{this.invoices}} 
      @subscription={{this.subscription}}
    />
  {{/if}}
{{/if}}

{{#if this.subscription.isStripe}}
  <section class="contact">
    {{#let this.subscription.billingInfo as |info|}}
      <Billing::ContactDetails 
        @openEditContactForm={{this.openEditContactForm}}
        @editContact={{this.editContact}}
        @account={{this.account}}
        @info={{info}}
      />
      <Billing::BillingDetails 
        @openEditBillingForm={{this.openEditBillingForm}}
        @editContact={{this.editContact}}
        @account={{this.account}}
        @info={{info}}
        @countries={{countries}}
      />
    {{/let}}
  </section>
{{/if}}

{{#if this.requiresSourceAction}}
  <span class="notice-banner--yellow" data-test-payment-error='true'>
    <SvgImage @name='icon-help' @class='icon-help icon-help-yellow' />
    There was a problem authorizing your card. Please retry.
  </span>
  {{#if this.retryAuthorization.isRunning}}
    <LoadingIndicator/>
  {{else}}
    <button class="button--green" onClick={{action (perform this.retryAuthorization)}}>
      Retry authorization
    </button>
  {{/if}}
{{else if this.requiresSource}}
  <span class="notice-banner--red" data-test-payment-error='true'>
    {{this.stripeErrorMessage}}
  </span>
  <TravisForm
    data-test-stripe-form='true' 
    @onSubmit={{perform this.retryPayment}} 
    as |form|
  >
    <StripeCard 
      @complete={{action "complete"}} 
      @options={{this.options}} 
      as |stripeError|
    >
      {{#if stripeError}}
        <p>{{stripeError.message}}</p>
      {{/if}}
    </StripeCard>
      <span class="notice-banner--white" data-test-help-text='true'>
        Credit card details are never stored on nor reach our servers. Payment data is handled by Stripe.
      </span>
    <div class='billing-checkout__payment'>
      {{#if this.retryPayment.isRunning}}
        <LoadingIndicator/>
      {{else}}
        <button class='button--green' {{action form.submit}} data-test-complete-payment='true'>
          Pay {{format-currency this.subscription.plan.price}}
        </button>
      {{/if}}
    </div>
  </TravisForm>
{{else if this.isPending}}
  <span class="notice-banner--yellow" data-test-trial-running-out='true'>
    We are currently waiting for Stripe to finish processing your order.
  </span>
{{else if (and this.isComplete this.subscription.isStripe)}}
  {{#if this.showPlansSelector}}
    <Billing::SelectPlan
      @title="Edit plan"
      @account={{this.account}}
      @selectedPlan={{this.selectedPlan}}
      @showAnnual={{this.showAnnual}}
      @showPlansSelector={{this.showPlansSelector}}
      @submit={{this.editPlan}}
      @showCancelButton={{true}}
    />
  {{else}}
    <div class="flex flex--wrap billing-subscription__buttons">
      {{#if this.isLoading}}
        <LoadingIndicator/>
      {{else}}
        <div class="billing-subscription__buttons--change">
          <BillingResubscribeButton 
            @account={{this.account}}
            @subscription={{this.subscription}}
            @resubscribe={{this.resubscribe}}
            @openPlansSelector={{fn (mut this.showPlansSelector) true}}
          />
        </div>
        <div class='billing-subscription__buttons--cancel'>
          {{#if this.canCancelSubscription}}
            <button {{on 'click' (fn (mut this.showCancelModal) true)}} class='button--white button--hover' data-test-open-cancel-subscription-modal='true'>
              Cancel subscription
            </button>
          {{/if}}
        </div>
      {{/if}}
    </div>
  {{/if}}
{{/if}}
 <Dialogs::CancelSubscriptionModal 
  @isOpen={{this.showCancelModal}}
  @onClose={{fn (mut this.showCancelModal) false}}
  @subscription={{this.subscription}}
/>

{{#if this.subscription.plan}}
  <section class="payment billing-subscription-section">
    <h4>
      Payment details
    </h4>
    {{#if this.subscription.isStripe}}
      {{#if this.openCreditCardForm}}
        <div class="edit-form">
          <TravisForm @onSubmit={{perform this.updateCreditCard}} as |form|>
            <StripeCard @complete={{action "complete"}} @options={{this.options}} as |stripeError|>
              {{#if stripeError}}
                <p>{{stripeError.message}}</p>
              {{/if}}
            </StripeCard>
            <div class="flex flex--wrap billing-subscription__buttons">
              {{#if this.updateCreditCard.isRunning}}
                <LoadingIndicator/>
              {{else}}
                <button data-test-update-contact-address-button='true' onclick={{form.submit}} class="button--green">
                  Update payment details
                </button>
                <a onClick={{action (toggle 'openCreditCardForm' this)}} class='link cancel-link'>
                  Cancel
                </a>
              {{/if}}
            </div>
          </TravisForm>
        </div>
      {{else}}
        <div class="flex flex--v-center">
          <SvgImage @name="icon-credit-card" @class="icon--color" />
          <p data-test-credit-card> 
            •••• •••• ••••
            {{this.subscription.creditCardInfo.lastDigits}}
          </p>  
        </div>
        {{#if this.account.hasSubscriptionPermissions}}
          <button class='button--blue billing-subscription__buttons' onClick={{action (toggle 'openCreditCardForm' this)}}>
            Edit payment details
          </button>
        {{else}}
          <div class='button disabled billing-subscription__buttons'>
            Edit payment details
          </div>
        {{/if}}
      {{/if}}
    {{/if}}
    {{#if this.subscription.isManual}}
      <ManualSubscriptionHelp />
    {{/if}}
  </section>
{{/if}}

{{#if this.isNotGithubTrial}}
  <div class='selected-plan__price'>
    <h6 class='selected-plan__price--total' data-test-plan-total='true'>
      Total:
    </h6>
    {{#if this.subscription.plan }}
      <p class='selected-plan__price--large' data-test-price>
        {{format-currency this.subscription.plan.price floor='true'}}
      </p>
      <span data-test-selected-plan-period='true' class='selected-plan__period'>
        {{if this.subscription.plan.annual '/year' '/month'}}
      </span>
    {{/if}}
  </div>
  {{#if this.discountIsValid}}
    <div class="discount" data-test-stripe-discount='true'>
      <h6>Discount: </h6>
      <p> 
        {{#if this.percentOff}}
          {{this.percentOff}}%
        {{else if this.amountOff}}
          ${{this.amountOff}}
        {{/if}}
        {{#if (eq this.duration 'forever')}}
          forever
        {{else}}
          off until {{moment-format (moment-add subscription.createdAt this.durationInMonths precision='months') 'MMMM YYYY'}}
        {{/if}}
      </p>
    </div>
  {{/if}}
{{/if}}

{{#if this.account.hasSubscriptionPermissions}}
  {{#if this.isStepOne}}
    <Billing::SelectPlan
      @title="Subscribe to a plan"
      @newSubscription={{this.newSubscription}}
      @account={{this.account}}
      @submit={{action 'next'}}
    />
  {{else if this.isStepTwo}}
    <Billing::Information
      @newSubscription={{this.newSubscription}}
      @goToFirstStep={{action 'goToFirstStep'}}
      @next={{action 'next'}}
      @back={{action 'back'}}
    />
  {{else if this.isStepThree}}
    <Billing::Payment
      @newSubscription={{this.newSubscription}}
      @account={{this.account}}
      @goToFirstStep={{action 'goToFirstStep'}}
      @back={{action 'back'}}
    />
  {{/if}}
{{else}}
  <p data-test-no-permission-message>
    You don't have permission to create a subscription
  </p>
{{/if}}

{{#if this.showPlansSelector}}
  <section class='billing-select-plan'>
    <TravisForm @onSubmit={{perform this.save}} as |form|>
      <h3 data-test-billing-info-title>
        {{this.title}}
      </h3>
      <div class='flex form-elem flex--v-center flex--wrap billing-plan-switch'>
        <div>
          <form.field
            @disableFrame={{true}}
            class='display-value-switch'
            @value={{this.showAnnual}}
            @onChange={{action "togglePlanPeriod"}} 
            as |Field|
          >
            <Field.switch 
              @text={{true}} 
              @offText='Monthly'
              @onText='Annual'
            />
          </form.field>
        </div>
        <div class='m-l-s'>
          {{#if this.showMonthly}}
            <h4>billing cycle recommendation:</h4>
            <p>
              Switch to annual billing to receive
              <span class='font--bolder'>one month free!</span>
            </p>
          {{else}}
            <p><span class='font--bolder'>One month free</span> now included!</p>
          {{/if}}
        </div>
      </div>
      <div class='plan-selector'>
        <h4 data-test-choose-plan-heading>Choose number of concurrent jobs</h4>
        <div class='billing-plans flex flex--wrap' data-test-billing-plan-choices>
          {{#each this.displayedPlans as |plan|}}
            <div
              data-test-plan-box='true'
              class='billing-plans__box pointer {{if (eq plan.id this.selectedPlan.id) 'highlight-plan'}}'
              onclick={{action (mut this.selectedPlan) plan}}
            >
              <p class='billing-plans__box--price' data-test-selected-plan-price="true">
                {{format-currency plan.price floor="true"}}
                <span class='color-asphalt-grey'>
                  {{if plan.annual '/year' '/month'}}
                </span>
              </p>
              <p class='billing-plans__box--name color-asphalt-grey' data-test-selected-plan-name="true">
                {{plan.name}}
              </p>
              <p class='billing-plans__box--jobs' data-test-selected-plan-jobs="true">
                {{pluralize plan.builds 'concurrent job'}}
              </p>
            </div>
          {{/each}}
        </div>
      </div>
      <div>
        {{#if this.isLoading}}
          <LoadingIndicator/>
        {{else}}
          <button data-test-subscribe-button
            onclick={{action form.submit}}
            class='{{if this.showCancelButton 'button--green' 'button--blue'}}'
          >
            Subscribe @{{this.account.login}} to {{selectedPlan.builds}} job plan
          </button>
          {{#if this.showCancelButton}}
            <a data-test-cancel-button
              onClick={{action (toggle 'showPlansSelector' this)}} class="link cancel-link"
            >
              Cancel
            </a>
          {{/if}}
        {{/if}}
      </div>
    </TravisForm>
  </section>
{{/if}}

{{#if this.showConfigValidation}}
  <div class="yml-messages">
    <div class="header" {{action 'toggle'}}>
      <span class="tooltip-wrapper level-icon">
        <SvgImage @name="msg-{{this.maxLevel}}"  @class={{this.iconClass}} />
        <EmberTooltip @text={{this.summary}} />
      </span>
      Build config validation
      {{#unless this.isExpanded}}
        &mdash; {{this.summary}}
      {{/unless}}
      <div class="tools">
        <ExternalLinkTo href="{{config-get 'urls.buildConfigValidation'}}">
          <SvgImage @name='icon-help' @class='icon-help' />
        </ExternalLinkTo>
        <SvgImage @name="icon-dropdown-arrow" @class="icon-toggle icon-{{this.toggleStatusClass}}" />
      </div>
    </div>
    {{#if this.isExpanded}}
      <div class="list">
        {{#each this.request.messages as |message|}}
          <BuildMessage @message={{message}} />
        {{/each}}
      </div>
    {{/if}}
  </div>
{{/if}}

{{! Product thinks that referencing Job Minutes in the title will be easier for people to understand than Build Minutes. }}
<InsightsGlance
  @isLoading={{this.isLoading}}
  @isEmpty={{this.isEmpty}}
  @title="Total Job Minutes"
  @statistic={{this.totalBuildText}}
  @labels={{this.labels}}
  @values={{this.buildMins}}
  @datasetTitle="Minutes"
  @centerline={{this.avgBuildMins}}
/>

{{#if this.build.number}}
  <LinkTo @route="build" @model={{this.build.id}}>
    <StatusIcon @status={{this.build.state}} />
    <span class="build-tile-number">
      #{{this.build.number}}
    </span>
  </LinkTo>
{{/if}}

{{#if this.isVisible}}
  <button
    class="button--blue"
    data-test-activate-button
    {{on 'click' (action 'activateAll')}}
  >
    <SvgImage @name='icon-repooctocat' @class="fill-current align-text-bottom w-5 h-5 ml-1 mr-2" />
    Activate all repositories using GitHub Apps
  </button>
{{/if}}

{{#if this.showUser}}
  <LinkTo @route="owner" @model={{this.user.login}}>
    <EmberTooltip @text={{concat "View repositories for " this.nameOrLogin}} />
    <UserAvatar
      @url={{this.user.avatarUrl}}
      @name={{this.user.name}}
      @small={{true}}
      @size={{18}}
    />
    <span class="label-align inner-underline">
      {{this.nameOrLogin}}
    </span>
  </LinkTo>
{{else if this.commit}}
  {{#if this.committerOnly}}
    <span class="label-align">
      {{this.commit.committerName}}
    </span>
  {{else}}
    {{#if this.commit.authorName}}
      <span class="label-align">
        {{this.commit.authorName}} authored{{if this.commit.authorIsCommitter ' and committed'}}
      </span>
    {{/if}}
    {{#unless this.commit.authorIsCommitter}}
      {{#if this.commit.committerName}}
        <span class="label-align">
          {{this.commit.committerName}} committed
        </span>
      {{/if}}
    {{/unless}}
  {{/if}}
{{/if}}

{{#if this.fetchRepo.isRunning}}
  <LoadingScreen @center={{true}} @margin={{true}} />
{{else}}
  <div class="container">
    <div data-test-email-unsubscribe-sadmail="true">
      <SvgImage @name="sadmail" @class="picture" />
    </div>
    {{#if this.isError}}
      <h1 class="title" data-test-email-unsubscribe-title>
        We didn't find the repository
        <br />
        that you wanted to unsubscribe from
      </h1>
      <p class="description" data-test-email-unsubscribe-description>
        Please make sure you have access to the repository and try again.
      </p>
      <div class="controls">
        <button
          class="button--white button--hover"
          onclick={{perform this.fetchRepo}}
          data-test-email-unsubscribe-primary-button
        >
          Try again
        </button>
      </div>
    {{else}}
      <h1 class="title" data-test-email-unsubscribe-title>
        Unsubscribe from
        <LinkTo
          @route="repo"
          @model={{this.repo}}
          @target="_blank"
          class="repo-link repo-slug"
        >
          {{this.repo.formattedSlug}}
        </LinkTo>
      </h1>
      <p class="description" data-test-email-unsubscribe-description>
        {{#if this.isSubscribed}}
          Please confirm that you would like to unsubscribe from build emails for the
        {{else}}
          You are unsubscribed from build emails for the
        {{/if}}
        <span class="repo-slug repo-slug--bold">
          {{this.repo.formattedSlug}}
        </span>
        repository.
        {{#if this.showConfigNote}}
          If you’re still receiving emails after unsubscribing, please check your
          <ExternalLinkTo @href="https://github.com/{{this.repo.slug}}/blob/{{this.repo.defaultBranch.name}}/.travis.yml">
            .travis.yml
          </ExternalLinkTo>
        {{/if}}
      </p>
      <div class="controls">
        <button
          class="{{if this.isSubscribed 'button--orange' 'button--white'}} button--hover control control--primary"
          disabled={{this.task.isRunning}}
          onclick={{action "processTask"}}
          data-test-email-unsubscribe-primary-button
        >
          {{#if this.isUnsubscribing}}
            Unsubscribing...
          {{else if this.isSubscribing}}
            Subscribing...
          {{else if this.isSubscribed}}
            Confirm unsubscription
          {{else if this.isUnsubscribed}}
            Re-subscribe
          {{/if}}
        </button>
        <LinkTo
          @route="repo"
          @model={{this.repo}}
          @tagName="button"
          @disabled={{this.task.isRunning}}
          class="no-button control"
          data-test-email-unsubscribe-secondary-button="true"
        >
          {{#if this.isSubscribed}}
            Cancel
          {{else}}
            Go to repository
          {{/if}}
        </LinkTo>
      </div>
      <p class="appendix" data-test-email-unsubscribe-appendix>
        You can always opt-in to receive emails for this repository
        again anytime by updating your
        <LinkTo @route="account.settings" class="settings-link">
          email notification settings
        </LinkTo>
      </p>
    {{/if}}
  </div>
{{/if}}

{{#if hasBlock}}
  {{yield}}
{{else}}
  {{this.content}}
{{/if}}

{{#if this.messages.firstObject}}
  <FlashItem
    data-test-components-flash-item="true"
    @flash={{this.messages.firstObject}}
    @close={{action "closeMessage"}}
  />
{{/if}}

{{#unless this.features.enterpriseVersion}}
  {{#if this.auth.signedIn}}
    <BroadcastTower
      @status={{this.broadcasts.lastBroadcastStatus}}
      @count={{this.broadcasts.length}}
      @toggleBroadcasts={{action "toggleBroadcasts"}}
    />
    <ul class="broadcasts {{if this.showBroadcasts 'is-open'}}">
      {{#each this.broadcasts.content as |broadcast|}}
        <li title="Transmitted on {{broadcast.created_at}}">
          <p>
            <span class="broadcast-status {{broadcast.category}}"></span>
