            <div class="flex flex--wrap billing-subscription__buttons">
              {{#if this.updateCreditCard.isRunning}}
                <LoadingIndicator/>
              {{else}}
                <button data-test-update-contact-address-button='true' onclick={{form.submit}} class="button--blue">
                  Edit payment details
