        </div>
        <button class='button--blue billing-subscription__buttons' onClick={{action (toggle 'openCreditCardForm' this)}}>Edit payment details</button>
      {{/if}}
    {{/if}}
    {{#if this.subscription.isManual}}
      <ManualSubscriptionHelp />
    {{/if}}
  </section>
