        <LinkTo @route="job" @models={{array job.repo job}}>
          <span class="label-align">
            {{job.repo.slug}}
          </span>
        </LinkTo>
      {{/if}}
      <p class="tile-title float-right">
        <SvgImage @name="icon-hash" @class="icon" />
        {{#if job.repo.slug}}
          <LinkTo @route="job" @models={{array job.repo job}}>
            <span class="label-align">
              {{job.number}}
            </span>
          </LinkTo>
        {{/if}}
      </p>
      <p>
        <SvgImage @name="icon-clock" @class="icon" />
        <span class="label-align">
          Queued
        </span>
      </p>
    </div>
