        aria-label="Restart {{this.type}}"
        type="button"
        {{action (perform this.restart)}}
      >
        {{#if this.tooltips}}
          <EmberTooltip class={{if this.mobilelabels "hidden-on-mobile"}}>
            Restart {{this.type}}
