  </UiKit::box>
{{else}}
  <UiKit::box @textAlign='center' data-test-sales-contact-success-container="true">
    <UiKit::Text @tag='h2' @variant='h2' data-test-sales-contact-success-title="true">
      Thank you!
    </UiKit::Text>
    <UiKit::box
      @maxWidth='xs'
      @margin={{hash y=4 x='auto'}}
      data-test-sales-contact-success-image="true"
    >
      <SvgImage @name="hard-hat-success" />
    </UiKit::box>
    <UiKit::Text data-test-sales-contact-success-body="true">
      We'll get back to you as soon as possible.
    </UiKit::Text>
  </UiKit::box>
{{/unless}}

{{#if this.isEmpty}}
  <SvgImage @name="icon-nobuilds" />
{{else}}
  {{#if this.hasPassed}}
    <SvgImage @name="icon-passed" />
  {{/if}}
  {{#if this.hasFailed}}
    <SvgImage @name="icon-failed" />
  {{/if}}
  {{#if this.wasCanceled}}
    <SvgImage @name="icon-canceled" />
  {{/if}}
  {{#if this.hasErrored}}
    <SvgImage @name="icon-errored" />
  {{/if}}
  {{#if this.isRunning}}
    <SvgImage @name="icon-running" />
  {{/if}}
{{/if}}

{{#let (element this.tag) as |Tag|~}}
  <Tag
    class={{this.allClasses}}
    ...attributes
  >{{yield}}</Tag>
{{~/let}}

{{#let (element this.tag) as |Tag|}}
  <Tag
    class={{this.allClasses}}
    ...attributes
  >{{yield (hash
    item=(component "ui-kit/grid-item" grid=this)
  )}}</Tag>
{{/let}}

{{#if this.isVisible}}
  <div data-test-visibility-settings-list>
    {{#each this.options as |option|}}
      {{#let (eq this.selectionKey option.key) as |selected|}}
        <button
          data-test-visibility-settings-list-item
          data-test-visibility-settings-list-item-selected={{selected}}
          autofocus={{and this.focusOnList selected}}
          class="visibility-setting-list-item {{if selected 'visibility-setting-list-item--selected'}}"
          onclick={{action (mut this.selectionKey) option.key}}
        >
          <span class="visibility-setting-list-item__radio"></span>
          <span class="visibility-setting-list-item__description" data-test-visibility-settings-list-item-description>
            {{option.description}}
          </span>
        </button>
      {{/let}}
    {{/each}}
  </div>
  <div class="visibility-setting-list-item visibility-setting-list-item--save">
    <button
      class="settings-button"
      onclick={{action "toggleConfirmationModal"}}
      disabled={{this.isChangeNeutral}}
      data-test-visibility-settings-submit
    >
      Save
    </button>
  </div>
  <Modal
    @isVisible={{this.isShowingConfirmationModal}}
    class="visibility-settings-modal"
    data-test-visibility-settings-modal="true"
  >
    <div class="visibility-settings-modal__top">
      <h3 class="visibility-settings-modal__header" data-test-visibility-settings-modal-title>
        {{#if this.isChangeNegative}}
          Restrict
        {{else if this.isChangePositive}}
          Increase
        {{else}}
          Update
        {{/if}}
        visibility of your private build insights
      </h3>
      <button
        type="button"
        title="Close modal"
        class="pointer visibility-settings-modal__close-button"
        autofocus={{this.focusOnModal}}
        onclick={{action "toggleConfirmationModal"}}
        data-test-visibility-settings-modal-close-button
      >
        <SvgImage @name="icon-failed" @class="icon" />
      </button>
    </div>
    <div class="visibility-settings-modal__body">
      <p data-test-visibility-settings-modal-description>
        {{#if this.isChangeNeutral}}
          Visibility update is in progress
        {{else if this.selection.modalText}}
          {{this.selection.modalText}}
        {{else}}
          This change will make your private build insights {{if this.isChangeNegative "only"}}
          available to {{this.selectionTitle}}
        {{/if}}
      </p>
    </div>
    <div class="visibility-settings-modal__controls">
      <button
        data-test-visibility-settings-modal-cancel-button
        role="button"
        class="button button--red"
        onclick={{action "toggleConfirmationModal"}}
      >
        Cancel
      </button>
      <button
        data-test-visibility-settings-modal-confirm-button
        role="button"
        class="button button--blue"
        onclick={{action "confirm"}}
      >
        Confirm
      </button>
    </div>
  </Modal>
{{/if}}

{{#unless this.isSuccess}}
  {{#if this.showSupportForm}}
    <h1 class="header-main align-center" data-test-zendesk-form-header>
      Get help from our support engineers
    </h1>
    <div class="subheading align-center">
      <span class="nowrap">
        Our support hours are between
      </span>
      <span class="nowrap" data-test-zendesk-form-support-hours>
        {{this.startTime}} {{this.timezone}} – {{this.endTime}} {{this.timezone}}
      </span>
      <span class="nowrap">
        Monday through Friday
      </span>
    </div>
    <div data-test-zendesk-form-container>
      <TravisForm
        @onSubmit={{action "handleSubmit"}}
        as |form|
      >
        <form.field
          @label="Email"
          @value={{this.email}}
          @required={{true}}
          @validator={{form-validator form.validators.email}}
          @onChange={{action (mut this.email)}}
          data-test-zendesk-form-email="true"
          as |field|
        >
          {{#if this.emails.length}}
            <field.select @options={{this.emails}} as |email|>
              {{email}}
            </field.select>
          {{else}}
            <field.input @disabled={{true}} />
          {{/if}}
        </form.field>
        <form.field
          @label="Subject"
          @value={{this.subject}}
          @required={{true}}
          @disabled={{this.isSubmitting}}
          @onChange={{action (mut this.subject)}}
          data-test-zendesk-form-subject="true"
          as |field|
        >
          <field.input @type="text" />
        </form.field>
        <form.field
          @label="Description"
          @value={{this.description}}
          @disabled={{this.isSubmitting}}
          @required={{true}}
          @validator={{form-validator
            form.validators.difference
            this.descriptionTemplate
            "Make sure to add some details on what you'd like to talk with us about"
          }}
          @autoValidate={{false}}
          @helperText="Let us know what's happening and how this is affecting you. Tip: Include any links to failing Travis CI build"
          @onChange={{action (mut this.description)}}
          as |field|
        >
          <field.textarea
            @rows={{10}}
            data-test-zendesk-form-description="true"
          >{{this.description}}</field.textarea>
        </form.field>
        <div class="align-center">
          <button
            onclick={{action form.submit}}
            class="submit-button button--blue button--large"
            data-test-zendesk-form-submit
          >
            {{#if this.isSubmitting}}
              <LoadingIndicator @white={{true}} />
            {{else}}
              Submit
            {{/if}}
          </button>
        </div>
      </TravisForm>
    </div>
  {{else if this.showLoginPrompt}}
    <h1 class="header-main align-center" data-test-zendesk-form-log-in-header>
      Get help from the experts
    </h1>
    <div class="subheading align-center">
      Logging into your account and sending an example of your problem will speed up getting an answer
    </div>
    <div class="sign-in-image align-center" data-test-zendesk-form-log-in-image>
      <SvgImage @name="hard-hat" />
    </div>
    <div class="sign-in align-center">
      <button
        title="Sign into Travis CI"
        class="hero-button"
        data-test-zendesk-form-log-in-button
        {{action (action "signIn" false target=auth)}}
      >
        <SvgImage @name="icon-repooctocat" />
        Sign in with GitHub
      </button>
    </div>
  {{else}}
    <h1
      class="header-main align-center"
      data-test-zendesk-form-community-header
    >
      Get help in our Community forum
    </h1>
    <div class="subheading align-center">
      Talk with developers and experts, submit
      <ExternalLinkTo @href={{this.featureRequestUrl}}>
        feature requests
      </ExternalLinkTo>,
      and more in our
      <ExternalLinkTo @href={{this.communityUrl}}>
        Community forum
      </ExternalLinkTo>
    </div>
    <div
      class="sign-in-image align-center"
      data-test-zendesk-form-community-image
    >
      <SvgImage @name="hard-hat" />
    </div>
  {{/if}}
{{else}}
  <h1 class="header-main align-center" data-test-zendesk-form-success-header>
    Thank you!
  </h1>
  <div class="success-image align-center" data-test-zendesk-form-success-image>
    <SvgImage @name="hard-hat-success" />
  </div>
  <div class="subheading align-center" data-test-zendesk-form-success-message>
    One of our support engineers will get back to you as soon as possible.
  </div>
  <div class="back-link align-center">
    <a class="button--blue" {{action "goBack"}} data-test-zendesk-form-back-link>
      Head back
    </a>
  </div>
{{/unless}}

{{#if this.message}}
